 import './App.css';
import styled from "styled-components";
import {useState} from "react";
 import {TwitterTimelineEmbed, TwitterTweetEmbed} from "react-twitter-embed";
const StyledButton= styled.button`
        width: 20vw;
         height: 10vh;
        background: aliceblue;
  border-radius: 40px;
 margin-top: 25px;
 `   ;

const StyledTextArea= styled.textarea`
  width: 20vw;
  height: 15vh;
  background: aliceblue;

`   ;
const StyledActionArea= styled.div`
    display: inline-flex;
  margin-bottom: 5vh;
`
 const Line =styled.div`
   border-top: 15px solid grey;
   -webkit-box-flex: 1;
   -webkit-flex-grow: 1;
   -ms-flex-positive: 1;
   flex-grow: 1;
   margin-bottom: 16px;;`
function App() {


    const [sendTweetInput, setSendTweetInput] = useState('');
    const [getTweetInput, setGetTweetInput] = useState('');
    const [removeTweetInput, setRemoveTweetInput] = useState('');


    const [sendTweetResponse, setSendTweetResponse] = useState('');
    const [getTweetResponse, setGetTweetResponse] = useState('');
    const [removeTweetResponse, setRemoveTweetResponse] = useState('');

    const onSendClick =()=>{
        var request = require('request');
        var options = {
            'method': 'POST',
            'url': 'https://u6l66oictd.execute-api.us-east-1.amazonaws.com/dev/tweet/create',
            'headers': {
                'Content-Type': 'application/json'
            },
            body: JSON.stringify({
                "text": "testing tweet 22221242222"
            })

        };
        request(options, function (error, response) {
            if (error) throw new Error(error);
            console.log(response.body);
        });


    }
    const onGetClick =()=>{

    }
    const onDeleteClick =()=>{

    }

  return (
    <>
        <StyledActionArea>
            <StyledButton>ACTION BUTTON</StyledButton>
            <StyledTextArea value={'INPUT TEXTAREA'}></StyledTextArea>
            <StyledTextArea value={'OUTPUT TEXTAREA'}></StyledTextArea>
        </StyledActionArea>

        <Line>
        </Line>
       <b> Please select from the options below and refer to the one above for illustration/usage</b>
        <Line/>
      <StyledActionArea>
        <StyledButton onClick={onSendClick}>SEND Tweet</StyledButton>
          <StyledTextArea placeholder={'please enter text and press button to send text as tweet. wait upto 1 minute for it to be shown on the right embedded twitter'}>{sendTweetInput}</StyledTextArea>
        <StyledTextArea>{sendTweetResponse}</StyledTextArea>
          <TwitterTimelineEmbed
              sourceType="profile"
              screenName="AkankshaRawat09"
              options={{height: 400}}
          />
      </StyledActionArea>


        <StyledActionArea>
        <StyledButton>GET Tweet</StyledButton>
          <StyledTextArea placeholder={'please enter text and press button to get all tweets containing text'}>{getTweetInput}</StyledTextArea>
          <StyledTextArea>{getTweetResponse}</StyledTextArea>      </StyledActionArea>
      <StyledActionArea>
        <StyledButton>DELETE Tweet</StyledButton>
          <StyledTextArea placeholder={'please enter id of tweet you want to delete'}>{removeTweetInput}</StyledTextArea>
          <StyledTextArea>{removeTweetResponse}</StyledTextArea>
            </StyledActionArea>
    </>
  );
}

export default App;
