 import './App.css';
import styled from "styled-components";
import {useState} from "react";
 import Axios from 'axios';
 import {TwitterTimelineEmbed, TwitterTweetEmbed} from "react-twitter-embed";
 import axios from "axios";
const StyledButton= styled.button`
        width: 20vw;
         height: 10vh;
        background: aliceblue;
  border-radius: 40px;
 margin-top: 25px;
 `   ;

const StyledTextArea= styled.textarea`
  width: 20vw;
  height: 15vh;
  background: aliceblue;

`   ;
const StyledActionArea= styled.div`
    display: inline-flex;
  margin-bottom: 5vh;
`
 const Line =styled.div`
   border-top: 15px solid grey;
   -webkit-box-flex: 1;
   -webkit-flex-grow: 1;
   -ms-flex-positive: 1;
   flex-grow: 1;
   margin-bottom: 16px;;`
function App() {


    const [sendTweetInput, setSendTweetInput] = useState('');
    const [getTweetInput, setGetTweetInput] = useState('');
    const [removeTweetInput, setRemoveTweetInput] = useState('');


    const [sendTweetResponse, setSendTweetResponse] = useState('');
    const [getTweetResponse, setGetTweetResponse] = useState('');
    const [removeTweetResponse, setRemoveTweetResponse] = useState('');

    const onSendClick = async ()=>{


        var data = JSON.stringify({
            "text": "testsss2ssss22"
        });

        var config = {
            method: 'post',
            url: 'http://127.0.0.1:5000/sendTweet',
            headers: {
                'Content-Type': 'application/json'
            },
            data : data
        };

        axios(config)
            .then(function (response) {
                console.log(JSON.stringify(response.data));
            })
            .catch(function (error) {
                console.log(error);
            });

        try {

        const data = JSON.stringify({
            "text": sendTweetInput
        });

            var config = {
                method: 'post',
                url: 'https://91yptvln44.execute-api.us-east-1.amazonaws.com/dev/sendTweet',
                headers: {
                    'Content-Type': 'application/json'
                },
                data : data
            };

   const res=  await   Axios(config);
setSendTweetResponse(JSON.stringify(res.data.output.data))
}
catch (e){
    setSendTweetResponse(JSON.stringify(e))

}


    };
    const onGetClick =()=>{

    };
    const onDeleteClick =()=>{

    };

  return (
    <>
        <StyledActionArea>
            <StyledButton>ACTION BUTTON</StyledButton>
            <StyledTextArea value={'INPUT TEXTAREA'}></StyledTextArea>
            <StyledTextArea value={'OUTPUT TEXTAREA/RESPONSE'}></StyledTextArea>
        </StyledActionArea>

        <Line>
        </Line>
       <b> Please select from the options below and refer to the one above for illustration/usage</b>
        <Line/>
      <StyledActionArea>
        <StyledButton onClick={onSendClick}>SEND Tweet</StyledButton>
          <StyledTextArea placeholder={'please enter text and press button to send text as tweet. wait upto 1 minute for it to be shown on the right embedded twitter'} onChange={event => {setSendTweetInput(event.target.value)}}>{sendTweetInput}</StyledTextArea>
        <StyledTextArea value={sendTweetResponse} ></StyledTextArea>
          <TwitterTimelineEmbed
              sourceType="profile"
              screenName="AkankshaRawat09"
              options={{height: 400}}
          />
      </StyledActionArea>


        <StyledActionArea>
        <StyledButton>GET Tweet</StyledButton>
          <StyledTextArea placeholder={'please enter text and press button to get all tweets containing text'}>{getTweetInput}</StyledTextArea>
          <StyledTextArea>{getTweetResponse}</StyledTextArea>      </StyledActionArea>
      <StyledActionArea>
        <StyledButton>DELETE Tweet</StyledButton>
          <StyledTextArea placeholder={'please enter id of tweet you want to delete'}>{removeTweetInput}</StyledTextArea>
          <StyledTextArea>{removeTweetResponse}</StyledTextArea>
            </StyledActionArea>
    </>
  );
}

export default App;
